// src/UploadImage.js

import React, { useState } from 'react';

function UploadImage({ onUpload }) {
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);

  const [processedImage, setProcessedImage] = useState(null); 

  const handleImageChange = (e) => {
    //resets all 
    setImage(e.target.files[0]);
    setError(null);
    setProcessedImage(null);
  };

  const fileToBase64 = (file) => {//converts uploaded image to base64
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  };

  const handleSubmit = async () => {
    if (!image) {
      setError('Please select an image.');
      return;
    }

    //api call
    try {
        //convert image to base64, append to formData as 'img0'
        const base64Image = await fileToBase64(image);
        const formData = new FormData();
        formData.append('img0', base64Image);
        const response = await axios.post('https://darylfunggg-xray-hand-joint-detection.hf.space/api/predict/',
        {
            "fn_index":0,
            "data" : formData,
            "session_hash": "knwj8al8dei"
        },
        {
            "content-type": "application/json"
        }
      );
      console.log(response.status)
      if(response.status === 200){
        onUpload(response.data);
      }else {
        setError(response.status);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('Failed to process image.');
    }
  };

  return (
    <div>
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleSubmit}>Submit</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default UploadImage;
